import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { MarketList } from '../../models/market';
import { HttpClient } from '@angular/common/http';

@Injectable({
    providedIn: 'root',
})
export class MarketsService {
    private marketItems = new BehaviorSubject<MarketList[]>([]);
    public marketItems$: Observable<MarketList[]>;

    private screenItems = new BehaviorSubject<MarketList[]>([]);
    public screenItems$: Observable<MarketList[]>;

    private filteredItems = new BehaviorSubject<MarketList[]>([]);
    public filteredItems$: Observable<MarketList[]>;

    private dataLength = new BehaviorSubject<number>(0);
    public dataLength$: Observable<number>;

    sortOrder: BehaviorSubject<string> = new BehaviorSubject('');
    pageNumber: BehaviorSubject<number> = new BehaviorSubject(0);
    pageSize: BehaviorSubject<number> = new BehaviorSubject(20);
    selection: BehaviorSubject<string> = new BehaviorSubject('All');
    sortEvent: BehaviorSubject<string> = new BehaviorSubject('');

    constructor(private http: HttpClient) {
        this.getMarketItems();
        this.marketItems$ = this.marketItems.asObservable();
        this.filteredItems$ = this.filteredItems.asObservable();
        this.screenItems$ = this.screenItems.asObservable();
        this.dataLength$ = this.dataLength.asObservable();
    }

    getMarketItems() {
        return this.http
            .get<MarketList[]>('assets/data/marketsData.json')
            .subscribe((x) => {
                const data = x.map((e) => {
                    return new MarketList(e);
                });
                this.marketItems.next(data);
                this.filteredItems.next(data);
                this.findMarkets();
                this.dataLength.next(data.length);
            });
    }

    findMarkets(): void {
        let pageNumber = this.pageNumber.getValue();
        let pageSize = this.pageSize.getValue();

        let res = [...this.filteredItems.getValue()];

        const initialPos = pageNumber * pageSize;
        res = res.slice(initialPos, initialPos + pageSize);

        this.screenItems.next(res);
    }

    sortData() {
        let arr = this.filteredItems.getValue();
        let sortEvent = this.sortEvent.getValue();
        let sortOrder = this.sortOrder.getValue();

        if (sortEvent === 'market_name') {
            // Spesific(case insensitive) sorting algorithm for source column
            if (sortOrder === 'asc') {
                arr.sort((a, b) =>
                    a[sortEvent].toLowerCase() > b[sortEvent].toLowerCase()
                        ? 1
                        : b[sortEvent].toLowerCase() >
                          a[sortEvent].toLowerCase()
                        ? -1
                        : 0
                );
            } else {
                arr.sort((a, b) =>
                    b[sortEvent].toLowerCase() > a[sortEvent].toLowerCase()
                        ? 1
                        : a[sortEvent].toLowerCase() >
                          b[sortEvent].toLowerCase()
                        ? -1
                        : 0
                );
            }
        } else {
            // Same sorting algorithm for the all other columns
            if (sortOrder === 'asc') {
                arr.sort((a, b) =>
                    a[sortEvent] > b[sortEvent]
                        ? 1
                        : b[sortEvent] > a[sortEvent]
                        ? -1
                        : 0
                );
            } else {
                arr.sort((a, b) =>
                    b[sortEvent] > a[sortEvent]
                        ? 1
                        : a[sortEvent] > b[sortEvent]
                        ? -1
                        : 0
                );
            }
        }

        this.filteredItems.next(arr);
        this.findMarkets();
    }

    filterPair() {
        let arrAll = [];
        let arrFiltered = [];

        let selection = this.selection.getValue();
        arrAll = this.marketItems.getValue();

        if (selection === 'All') {
            this.filteredItems.next([...arrAll]);
            this.dataLength.next(arrAll.length);
            this.findMarkets();
        } else {
            arrAll.find((e) => {
                if (e.pairs.split('/')[1] === selection) {
                    arrFiltered.push(e);
                }
            });
            this.filteredItems.next([...arrFiltered]);
            this.dataLength.next(arrFiltered.length);
            this.findMarkets();
        }
    }
}

import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { Coin } from '../../models/coin.model';
import {
    fakeCoin,
    fakeCoinStatistics,
    fakeCoinVotes,
} from '../../../assets/data/coinData';
import { VoteCoinData } from '../../models/voteCoinData.model';
import {
    CalendarDateRange,
    ChartData,
    ChartDataTab,
    ChartDateRange,
} from '@coin-market/data';
import { OverviewPrice, OverviewMarketcap } from '@coin-market/data';
import { OverviewPriceStatistics } from '../../models/overview-price-statistics.model';

@Injectable({
    providedIn: 'root',
})
export class OverviewService {
    private overviewPrice = new BehaviorSubject<OverviewPrice[]>(null);
    private overviewMarketcap = new BehaviorSubject<OverviewMarketcap[]>(null);

    private coin = new BehaviorSubject<Coin>(null);
    public coin$: Observable<Coin>;

    private coinVotes = new BehaviorSubject<VoteCoinData>(null);
    public coinVotes$: Observable<VoteCoinData>;

    private coinStatistics: BehaviorSubject<OverviewPriceStatistics> = new BehaviorSubject(
        null
    );
    public coinStatistics$: Observable<OverviewPriceStatistics>;

    private chartData: BehaviorSubject<ChartData> = new BehaviorSubject(null);
    public chartData$: Observable<ChartData>;

    private _selectedTab = new BehaviorSubject<ChartDataTab>(
        new ChartDataTab()
    );
    private _selectedRange = new BehaviorSubject<ChartDateRange>(
        new ChartDateRange()
    );
    private _selectedCalendarRange = new BehaviorSubject<CalendarDateRange>(
        new CalendarDateRange()
    );

    public selectedTab$: Observable<ChartDataTab>;
    public selectedRange$: Observable<ChartDateRange>;
    public selectedCalendarRange$: Observable<CalendarDateRange>;

    //holds fake data, delete after http implementation
    price: OverviewPrice[] = [];
    price_coin: number[][] = [];
    marketcap: OverviewMarketcap[] = [];
    marketcap_coin: number[][] = [];

    /*
  This service is used for state of active coin
  */
    constructor() {
        this.fakeChartData();
        this.getOverviewPrice();
        this.getOverviewMarketcap();

        this.coin$ = this.coin.asObservable();
        this.getCoin();

        this.chartData$ = this.chartData.asObservable();
        this.mapChartData();

        this.coinVotes$ = this.coinVotes.asObservable();
        this.getCoinVotes();

        this.coinStatistics$ = this.coinStatistics.asObservable();
        this.getCoinStatistics();

        this.selectedTab$ = this._selectedTab.asObservable();
        this.selectedRange$ = this._selectedRange.asObservable();
        this.selectedCalendarRange$ = this._selectedCalendarRange.asObservable();
    }

    set selectedTab(val: ChartDataTab) {
        this._selectedTab.next(val);
    }

    set selectedRange(val: ChartDateRange) {
        this._selectedRange.next(val);
    }

    set selectedCalendarRange(val: CalendarDateRange) {
        this._selectedCalendarRange.next(val);
    }

    /*
  This method sends a request to get active Coin Detail
  Params(@coindID)
  Response(Coin)
  */
    getCoin(): void {
        this.coin.next(fakeCoin);
    }

    getCoinVotes(): void {
        this.coinVotes.next(fakeCoinVotes);
    }

    fakeChartData(): void {
        for (let i = 500; i > 0; i--) {
            const day = new Date().getDate();
            this.price.push({
                date_time: new Date(new Date().setDate(day - i)).toString(),
                price: Math.floor(Math.random() * 100000 + 1),
                vol_24h: Math.floor(Math.random() * 10000000 + 1),
            });
            this.marketcap.push({
                date_time: new Date(new Date().setDate(day - i)).toString(),
                marketCap: Math.floor(Math.random() * 90000000000 + 1),
                vol_24h: Math.floor(Math.random() * 10000000 + 1),
            });

            this.price_coin.push([new Date().setDate(day - i), 1]);
            this.marketcap_coin.push([
                new Date().setDate(day - i),
                Math.floor(Math.random() * 90000000 + 1),
            ]);
        }
    }

    getOverviewPrice(): void {
        this.overviewPrice.next(this.price);
    }

    getOverviewMarketcap(): void {
        this.overviewMarketcap.next(this.marketcap);
    }

    getCoinStatistics(): void {
        const { coin_id, circulating_supply } = this.coin.getValue();
        this.coinStatistics.next(
            new OverviewPriceStatistics({
                ...fakeCoinStatistics,
                coin_id,
                circulating_supply,
            })
        );
    }

    private mapChartData() {
        const price = this.overviewPrice.getValue();
        const marketcap = this.overviewMarketcap.getValue();
        const coin = this.coin.getValue();

        this.chartData.next(new ChartData(coin.coin_name, price, marketcap));
    }
}

