export class CalendarDateRange {
    start: Date;
    end: Date;

    constructor(start?: Date, end?: Date) {
        this.start = start || null;
        this.end = end || null;
    }
}

import { OverviewMarketcap } from './overview-marketcap.model';
import { OverviewPrice } from './overview-price.model';

export class ChartData {
    coinName: string;
    price: { usd: number[][]; coin: number[][] } = { usd: [], coin: [] };
    marketCap: { usd: number[][]; coin: number[][] } = { usd: [], coin: [] };
    volume: number[][] = [];

    constructor(
        coinName: string,
        price: OverviewPrice[],
        marketcap: OverviewMarketcap[]
    ) {
        this.coinName = coinName;

        price.map((i) => {
            this.price.usd.push([this.formatDate(i.date_time), i.price]);
            this.volume.push([this.formatDate(i.date_time), i.vol_24h]);
        });

        marketcap.map((i) => {
            this.marketCap.usd.push([
                this.formatDate(i.date_time),
                i.marketCap,
            ]);
        });
    }

    formatDate(date: string) {
        //todo date validation
        return new Date(date).getTime();
    }
}

export class ChartDataTab {
    text: 'Price' | 'Market Cap';
    textJson: 'price' | 'marketCap';

    constructor(item?: any) {
        (this.text = item?.text || 'Price'),
            (this.textJson = item?.textJson || 'price');
    }
}

import { CalendarDateRange } from '../ui-calendar/date-range.model';

export class ChartDateRange {
    value: '1D' | '7D' | '1M' | '3M' | '1Y' | 'ALL' | 'YTD';
    dateRange: CalendarDateRange;

    constructor(item?: any) {
        this.value = item?.value || 'ALL';
        this.dateRange = item?.dateRange || new CalendarDateRange();
    }
}

import { CoinSupplyNumbers } from './coin-supply-numbers.model';

export class Coin {
    coin_id: string;
    price: number;
    coin_name: string;
    market_cap: ValuePercentage;
    fully_diluted_market_cap: ValuePercentage;
    volume: ValuePercentage;
    circulating_supply: CoinSupplyNumbers;
    price_high_low: PriceHighLow;
    icon: string;

    constructor(item?: any) {
        this.coin_id = item?.coin_id || '';
        this.price = item?.price || 0;
        this.coin_name = item?.coin_name || '';
        this.market_cap = new ValuePercentage(item?.market_cap);
        this.fully_diluted_market_cap = new ValuePercentage(
            item?.fully_diluted_market_cap
        );
        this.volume = new ValuePercentage(item?.volume);
        this.circulating_supply = new CoinSupplyNumbers(
            item?.circulating_supply
        );
        this.price_high_low = new PriceHighLow(item?.price_high_low);
        this.icon = item.icon || '';
    }
}

class ValuePercentage {
    value: number;
    change_percentage: number;

    constructor(item?: any) {
        this.value = item?.value || 0;
        this.change_percentage = item?.change_percentage || 0;
    }
}

class PriceHighLow {
    high: number;
    low: number;
    interval: string;

    constructor(item?: any) {
        this.high = item?.high || 0;
        this.low = item?.low || 0;
        this.interval = item?.interval || '';
    }
}

export class Coin {
    name: string;
    shortName: string;
    icon: string;
    price: number;
    rank: number;
    watchlists: number;

