import { AfterViewInit, Component } from '@angular/core';
import {
    RouteConfigLoadEnd,
    RouteConfigLoadStart,
    Router,
} from '@angular/router';

@Component({
    selector: 'coin-market-loading-bar',
    templateUrl: './loading-bar.component.html',
    styleUrls: ['./loading-bar.component.scss'],
})
export class LoadingBarComponent implements AfterViewInit {
    element: Element;
    elementClass = 'loading-progress-bar__bar';

    constructor(private router: Router) {}

    ngAfterViewInit(): void {
        this.element = document.querySelector('.' + this.elementClass);

        this.router.events.subscribe((event) => {
            if (event instanceof RouteConfigLoadStart) {
                this.element.classList.add('start');
            } else if (event instanceof RouteConfigLoadEnd) {
                this.element.classList.add('end');
                setTimeout(
                    () => (this.element.className = this.elementClass),
                    500
                );
            }
        });
    }
}

styleUrlsData.data:
.loading-progress-bar {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 5px;
    background-color: transparent;

    &__bar {
        height: 100%;
        width: 0;
        background-color: var(--color-primary-blue);

        &.start {
            animation: barStart 2s linear 0s 1 normal forwards;
        }

        &.end {
            animation: barEnd 0.4s linear 0s 1 normal forwards;
        }
    }
}

@keyframes barStart {
    100% {
        width: 40%;
    }
}

@keyframes barEnd {
    0% {
        width: 40%;
    }
    100% {
        width: 100%;
        opacity: 0;
    }
}

